import '../../src/components/Page/Page.css';
import Header from './Header/Header';
import LoginFrame from  './LoginFrame/LoginFrame'

import {Route, Routes} from "react-router-dom";
import MainApi from "../utils/MainApi";
import { useNavigate } from 'react-router-dom';


function App() {

    const history = useHistory();

    function onLogin() {
        MainApi.authorize()
            .then((res) => {
                if (res) {

                    // setIsOpenPopup(true);
                    // setPopupImage(success);
                    // setPopupTitle("Вы успешно авторизовались!");
                    // setLoggedIn(true);

                    history.push('/mainMenu')
                }
            })
            .catch((err) => {
                // setIsOpenPopup(true);
                // setPopupImage(fail);
                // setPopupTitle('Ошибка авторизации!' + err);
            });
    };


    return (
        <div className="page">
            <div className="page__content">
                <Header
                    authOn={false}/>
                <main>
                    <Routes>
                        <Route path="/login" element={ <LoginFrame
                            onLogin = {onLogin}
                        />} />
                    </Routes>

                </main>
            </div>
        </div>
    );
}

export default App;
